import React, { useState, useEffect } from 'react';

function OutputComponent({ userInput, setSelectedValue, fetchData }) {
  const [selectedOption, setSelectedOption] = useState('');
  const [positiveText, setPositiveText] = useState('');
  const [negativeText, setNegativeText] = useState('');

  const handleProcessClick = (option) => {
    if (option === selectedOption) {
      setSelectedOption('');
      setSelectedValue('');
    } else {
      setSelectedOption(option);
      setSelectedValue(option);
      // Fetch data from Flask server based on the selected option
      fetchData(option);
    }
  };

  useEffect(() => {
    setPositiveText('');
    setNegativeText('');
  }, [selectedOption]);

  return (
    <div className="output-component">
      <div className="output-buttons">
        <select
          value={selectedOption}
          onChange={(e) => handleProcessClick(e.target.value)}
        >
          <option value="">시스템을 선택하세요.</option>
          <option value="유리 / 불리 판단">유리 / 불리 판단</option>
          <option value="키워드분석">키워드 분석</option>
        </select>
      </div>
      <div className="output-box">
        {selectedOption === '유리 / 불리 판단' && (
          <>
            <div className="output-box2">
              <div className="positive-text">
                <h3>유리한 텍스트</h3>
                <p>{positiveText}</p>
              </div>
              <div className="negative-text">
                <h3>불리한 텍스트</h3>
                <p>{negativeText}</p>
              </div>
            </div>
          </>
        )}
        {selectedOption === '키워드분석' && (
          <>
            <p>{userInput}</p>
          </>
        )}
      </div>
    </div>
  );
}
export default OutputComponent;
